import { MainGridFather as MainGrid } from "../system_js/mainGrid.min.js";
class App
{
    id;
    name;
    THEME;
    isMath;
    tools;
    data;
    constructor(data)
    {
        this.id = data.id;
        this.name = data.name;
        this.isMath = data.math;
        this.theme(data.theme);
        console.log(data.grids);
        for(let gr of data.grids)
        {
            this.createGrid(gr);
        }
        this.data = data;
    }
    createGrid(gridData)
    {
        new MainGrid(gridData);
    }
    theme(themeName)
    {
        document.documentElement.removeAttribute("theme");
        document.documentElement.setAttribute("theme", themeName);
        this.THEME = themeName;
    }
}
export{App};